import { useState } from "react";
import { api } from "./api";

const btn = {
  padding: "8px 16px",
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer"
};

export default function App() {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [msg, setMsg] = useState("");
  const [slot, setSlot] = useState("");
  const [eta, setEta] = useState(null);

  async function sendOTP() {
    await api("/api/auth/send", "POST", { email });
    setMsg("OTP Sent");
  }

  async function verifyOTP() {
    await api("/api/auth/verify", "POST", { email, otp });
    setMsg("Login Successful");
  }

  async function bookSlot() {
    await api("/api/availability", "POST", {
      doctorId: "doc1",
      day: "Monday",
      slots: [slot]
    });
    setMsg("Slot Booked");
  }

  async function bookAmbulance() {
    const res = await api("/api/ambulance/book", "POST", {
      patientName: "User",
      pickupLocation: "Dhaka"
    });
    setEta(res.eta);
  }

  return (
    <div style={{ padding: 20 }}>
      <h1>HealthConnect</h1>

      <h2>OTP Login</h2>
      <input placeholder="Email" onChange={e => setEmail(e.target.value)} />
      <button onClick={sendOTP}>Send OTP</button><br />
      <input placeholder="OTP" onChange={e => setOtp(e.target.value)} />
      <button onClick={verifyOTP}>Verify</button>
      <p>{msg}</p>

      <h2>Doctor Slot</h2>
      <input type="datetime-local" onChange={e => setSlot(e.target.value)} />
      <button onClick={bookSlot}>Book Slot</button>

      <h2>Ambulance</h2>
      <button onClick={bookAmbulance}>Book Ambulance</button>
      {eta && <p>Live ETA: {eta} minutes</p>}

      <h2>ðŸ“„ E-Prescription</h2>
      <button style={btn}>Generate QR</button>
      <button style={{ ...btn, marginLeft: 10 }}>Download PDF</button>
      <p style={{ marginTop: 10 }}>
        âœ” QR-based verification supported
      </p>
    </div>
  );
}
